<?phpnamespace Admin\View\Helper;use Zend\View\Helper\AbstractHelper;class RenderAlert extends AbstractHelper {    const ALERT_INFO = 'alert_info';//info message    const ALERT_SUCCESS = 'alert-success';//success message    const ALERT_DANGER = 'alert-danger';//error messge    const ALERT_DEFAULT = 'alert-warning';//warning message    protected $enabledClose = true;    public function __invoke($enabledClose = true) {        $this->enabledClose = $enabledClose;    	$m = $this->view->flashMessages();    	$alert = '';    	if(!empty($m['error'])) {    	   $content = $this->_messageHtml($m['error']);    	   $alert .= $this->_alertHtml($content, self::ALERT_DANGER);    	}    	if (!empty($m['success'])) {    	   $content = $this->_messageHtml($m['success']);    	   $alert .= $this->_alertHtml($content, self::ALERT_SUCCESS);    	}    	if (!empty($m['default'])) {    		$content = $this->_messageHtml($m['default']);    	    $alert .= $this->_alertHtml($content, self::ALERT_DEFAULT);    	}    	if (!empty($m['info'])) {    	    $content = $this->_messageHtml($m['info']);    	    $alert .= $this->_alertHtml($content, self::ALERT_INFO);    	}    	return $alert;    }    /**     * create button html     * @param array|string $message     * @return string     */    protected function _messageHtml($message) {        $showMessge = '';        if (is_array($message)) {        	$showMessge = $this->view->HtmlList($message);        } else {            $showMessge = $message;        }        if ($this->enabledClose) {            $html = "<button type='button' class='close' data-dismiss='alert'><span aria-hidden='true'>"                ."&times;</span><span class='sr-only'>Close</span></button>{$showMessge}";        } else {        	return $showMessge;        }        return $html;    }
    /**     * Combine HTML alert      * @param string $content     * @param string $type     * @return string     */    protected function _alertHtml($content, $type = self::ALERT_INFO) {    	$class = 'alert';    	if ($type) {    		$class .= ' ' . $type;    	}    	if ($this->enabledClose) {    		$class .= " alert-dismissible";    	}    	return "<div class='{$class}' role='alert'>{$content}</div>";    }
}